import React, {useEffect,useContext} from 'react';
import { Alert,Button, Card, Container, Modal ,Col, Row } from "react-bootstrap";
import data from '../data.json'
import BarcodeScannerComponent from "react-webcam-barcode-scanner";



const BarcodeReader = () => {
    
    const [ barcodeData, setData ] = React.useState('No Barcode Found');
    const {matchProduct} = data.products.filter(x=>x.id === barcodeData)

    
    return (
      <div>
      <Card>
      <Card.Title variant="info">
      <Alert variant="info">Scan Your Product Here</Alert>
      </Card.Title>
     <Card.Body >
      <BarcodeScannerComponent
        width={300}
        height={200}
        onUpdate={(err, result) => {
          if(!err){
          if (result) {setData(
              result.text
            )
          }
          else setData('No Barcode Found')}
        }}
      />
      
      </Card.Body>
      <Card.Footer >
      <i class="far fa-times-circle"></i>
      <p onChange={()=> this.props.showScannedProduct(matchProduct)}>{barcodeData} </p>
      </Card.Footer>
      </Card>
      


      </div>
    )
}


export default BarcodeReader
