import React, {useEffect,useContext} from 'react';
import './App.css';
import data from './data.json'
import { Provider } from "react-redux";
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import { createStore, combineReducers } from "redux";
import { Card,Alert } from 'react-bootstrap';
import {
  Cart,
  Product,
  CheckoutButton,
  cartLocalization,
  cartReducer,
  setCartCurrency
} from "react-shopping-cart";


const store = createStore(
  combineReducers({
    cart: cartReducer
    // Your own reducers, sir
  })
);

class App extends React.Component {
  constructor(){
    super();
    this.state={
      products: data.products,
      barcodeData,
       setData : "Not Found"
    }
  }
 render (){
  const {
    product,
    getCheckoutButtonLocalization,
    getProductLocalization,
    getCartLocalization 
  } = this.state;

  const checkoutButtonElement = (
    <CheckoutButton
      getLocalization={getCheckoutButtonLocalization}
      checkoutURL="/to/my/checkout"
    />
  );
    return(
    <div className="App">

    <p>Header </p>
    <p>Scanner </p>
    <p>Product </p>
    <Card style={{height:'25rem',marginTop:'0rem'}} >
      <Card.Title variant="info">
      <Alert variant="info">Scan Your Product Here <span><i className="fa fa-search"></i>
      </span>
      </Alert>
      </Card.Title>
     <Card.Body >
      <BarcodeScannerComponent
      style={{  margin: "auto",
      padding:"0rem"}}
        width={300}
        height={200}
        onUpdate={(err, result) => {
          if(!err){
          if (result) {setData(
              result.text
            )
          }
          else setData('Not Found')}
        }}
      />

      </Card.Body>
      <Card.Footer >
      <Alert variant="alert"  id='#productInfo'>{barcodeData}</Alert>
      </Card.Footer>
      </Card>
    <p>Footer </p>
    <Provider store={store}>
        <div className="container">
          <Product
            {...product}
            checkoutButton={checkoutButtonElement}
            getLocalization={getProductLocalization}
          />
          <Cart
            checkoutButton={checkoutButtonElement}
            getLocalization={getCartLocalization}
          />
        </div>
    </Provider>

    </div>
);
}
}

export default App;
