import React, { Component } from 'react';
import { Card } from 'react-bootstrap';
import { useBarcode } from '@createnextapp/react-barcode';
class ExitCode extends Component {
barcodeGenerator=()=>{
    const { inputRef } = useBarcode({
        value: 'createnextapp',
        options: {
          background: '#ccffff',
        }
      });

      return <svg ref={inputRef} />;
    }
    render() { 
        return ( <div>
            <Card>
                <Card.Header>
                    Thank You!
                </Card.Header>
                <Card.Body>
                    {this.barcodeGenerator}
                </Card.Body>
            </Card>
        </div> );
    }
}
 
export default ExitCode;
