import React, {useEffect,useContext} from 'react';
import { Alert,Button, Card, Container, Modal ,Col, Row, Table, Badge } from "react-bootstrap";
import data from '../data.json'
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import { render } from '@testing-library/react';



const BarcodeReader = () => {
    
    const [ barcodeData, setData ] = React.useState('No Barcode Found');
    const products = data.products
    const scannedProduct = data.products.filter(product=>product.id===barcodeData)

    return (
      <div>
      <Card>
      <Card.Title variant="info">
      <Alert variant="info">Scan Your Product Here</Alert>
      </Card.Title>
     <Card.Body >
      <BarcodeScannerComponent
        width={300}
        height={200}
        onUpdate={(err, result) => {
          if(!err){
          if (result) {setData(
              result.text
            )
          }
          else setData('No Barcode Found')}
        }}
      />
      
      </Card.Body>
      <Card.Footer >
      </Card.Footer>
      </Card>
      <Modal>
        {scannedProduct.map(filteredProduct =>(
          <Modal.Dialog>
          <Modal.Header closeButton>
            <Modal.Title>{filteredProduct.title}</Modal.Title>
          </Modal.Header>
        
          <Modal.Body>
            <p>{filteredProduct.description}</p>
            <Badge>{filteredProduct.price}</Badge>
          </Modal.Body>
        
          <Modal.Footer>
            <Button variant="secondary">Close</Button>
            <Button variant="primary">Save changes</Button>
          </Modal.Footer>
        </Modal.Dialog>
        ))}
      </Modal>

      </div>
    )
}


export default BarcodeReader
