import React, {useEffect,useContext} from 'react';
import { Alert,Button, Card, Container, Modal ,Col, Row, Table, Badge } from "react-bootstrap";
import data from '../data.json'
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import { render } from '@testing-library/react';
import Products from "./Products";


const BarcodeReader = () => {
    
    const [ barcodeData, setData ] = React.useState('No Barcode Found');
    const products = data.products
    return (
      <div>
      <Card>
      <Card.Title variant="info">
      <Alert variant="info">Scan Your Product Here</Alert>
      </Card.Title>
     <Card.Body >
      <BarcodeScannerComponent
        width={300}
        height={200}
        onUpdate={(err, result) => {
          if(!err){
          if (result) {setData(
              result.text
            )
          }
          else setData('No Barcode Found')}
        }}
      />
      
      </Card.Body>
      <Card.Footer >
        {barcodeData}
      </Card.Footer>

      <div>
        hi
      {products.filter(product=>product.id==="DRS1").map(filteredProduct =>(
        <li>
          {filteredProduct.price}
        </li>
      ))}
      </div>

      </Card>



      </div>
    )
}


export default BarcodeReader
