import React, { Component } from 'react';
import { Modal } from 'react-bootstrap';

class Products extends Component {
      render() { 
        return ( 
        <Modal>
            <Modal.Dialog>
            {this.props.products.map(product =>(
                <li key={product.id}>
                    <p>{product.title}</p>
                    <button onClick={()=> this.props.addToCart(product)} >
                        Add To Cart
                    </button>
                </li>
            ))}
            </Modal.Dialog>
        </Modal> 
        );
    }
}

export default Products;
